import React from 'react';
import {
	View,
	
	StyleSheet,
	Text,
	FlatList,
	Image,
	Alert,
	CheckBox,
	TouchableOpacity
} from 'react-native';

class OrderScreen extends React.Component{
	state = {
		products: [{
			itemID: "itemID_0", quantity: '5', on: false, imageSrc:
			"data:image/png;base64,"
		},{
			itemID: "itemID_1", quantity: '5', on: false, imageSrc:
			"data:image/png;base64,"
		},{	
			itemID: "itemID_2", quantity: '5', on: false, imageSrc:
			"data:image/png;base64,"
		},{
			itemID: "itemID_3", quantity: '5', on: false, imageSrc:
			"data:image/png;base64,"
		},{
			itemID: "itemID_4", quantity: '5', on: false, imageSrc:
			"data:image/png;base64,"
		},{
			itemID: "itemID_6", quantity: '5', on: false, imageSrc:
			"data:image/png;base64,"
		}]
	}
	
	render(){
		return(
			<View style={{
				flex:1,
				flexDirection: 'column'
			}}>
				<View 
					style={{
						height: 50,
						backgroundColor: '#FF0000'
				}}>
			{/*	stripe with Item-Quantity-on	*/}
					<View style={[styles.alignCenter,{
						flex: 1,
						flexDirection: 'row'
					}]}>
						<Text style={[styles.labelText,styles.itemWidth]}>
							Item
						</Text>
						<Text style={[styles.labelText,styles.quantityWidth]}>
							Quantity
						</Text>
						<Text style={[styles.labelText,styles.onWidth]}>
							On
						</Text>
					</View>
				</View>
				<FlatList
					ItemSeparatorComponent={() =>
						<View
							style={{ height: 1, width: "100%", backgroundColor: "lightgray" }}
						/>
					}
					data={this.state.products}
					keyExtractor={item => item.itemID}
					renderItem={({ item }) =>
						<View style={[styles.alignCenter,{flexDirection: "row"}]}>
			{/*	flatlist of selected orders	*/}
							{/*	one row in flatlist for one item	*/}
							<TouchableOpacity
								style={styles.itemWidth}
								onPress={() => {
									Alert.alert("item screen for:\n"+item.itemID)
								}}
							>
								<View style={[styles.alignCenter,{ flexDirection: "row" }]}>
			{/*	region for item info	*/}
									<Image
										style={{ width: 50, height: 50 }}
										source={{ uri: item.imageSrc }}
									/>
									<Text style={{ padding: 10 }}>{item.itemID}</Text>
								</View>
							</TouchableOpacity>
						
							<View style={[styles.quantityWidth,styles.alignCenter]}>
			{/*	region for quantity of items	*/}
								<Text>{item.quantity}</Text>
							</View>
							<View style={[styles.onWidth,styles.alignCenter]}>
			{/*	region for on check box	*/}
							<CheckBox/>
							</View>
						</View>
					}
				/>
			</View>
		);
	}
}
export default OrderScreen;

const styles = StyleSheet.create({
	labelText:{
		textAlign: 'center',
		color: '#FFFFFF',
		fontSize: 20
	},
	itemWidth:{
		width: '60%'
	},
	quantityWidth:{
		width: '25%'
	},
	onWidth:{
		width: '15%'
	},
	alignCenter:{
		alignItems: 'center'
	}
})